#include <iostream>
#include <chrono>
#include <ctime>
#include <string>

std::string timeToString(time_t time) {
  std::tm* now = std::localtime(&time);
  int seconds = std::difftime(time, *now);
  int microseconds = seconds * 1000;
  int bits = microseconds / 1000000;
  int decisecs = bits / 1000;
  int millisecs = decisecs / 1000;
  int microsecs = millisecs * 1000;
  std::string result = "@";
  result += std::to_string(bits) + " " + std::to_string(decisecs) + " " + std::to_string(millisecs) + " " + std::to_string(microseconds);
  return result;
}
```

